import React, { Component } from 'react';
import { Redirect } from 'react-router';

export default class Login extends Component {
  constructor() {
    super();
    this.state = {
      loginComplete: false,
    }
  }


  handleClick = () => {
    this.setState({
      loginComplete: true,
    });
    const { loginComplete } = this.state;
    if(loginComplete) {
      <Redirect to="/" />
    }
  };

  render() {

    return (
      <div>
        <input type="email" />
        <input type="password" />
        <button type="button" onClick={ this.handleClick }>Login</button>
      </div>
    )
  }
}
