const INITIAL_STATE = {
  signUpUsers: [],  
}

const loginReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case '':
      return {
         ...state,
          signUpUsers: [{
          user: action.user,
          email: action.email,
          password: action.password,
        }]
      }
      break;
    default:
      return state;
  }
}

export default loginReducer;
